onClipEvent (load) {
	doorplace = "C-9-1";
	locked = true;
	keytype = 2;
	if (_root.BowserKey2 == true || _root.CheatCastleUnlock == true) {
		removelock = true;
	}
	//                              
	doorlockfunction = function () {
		_root.Course.Char.attack = true;
		_root.Course.Char.attackFrame = "Sign";
		_root.Course.Char.xspeed = 0;
		//
		_root.PlayMessage("This door is locked; you need a key from Bowser to open it!");
		_root.Mariosound = new Sound(this);
		_root.Mariosound.attachSound("Bowser - Laugh Muffled");
		_root.Mariosound.start(0, 1);
		_root.Mariosound.setVolume(_root.MarioVolume);
		count = 0;
		onEnterFrame = function () {
			X = _x+_parent._x;
			_root.Course.Char._x += (X-_root.Course.Char._x)/5;
			//
			_root.playpause = true;
			//
			count++;
			if (_root.KeySPACE() && count>5) {
				_root.StopMessage();
				//
				count = 0;
				onEnterFrame = function () {
					count++;
					if (count>5) {
						_root.playpause = false;
						_root.Course.Char.attack = false;
						onEnterFrame = null;
						gotoAndStop(1);
					}
				};
			}
		};
	};
}

